encode(String text) {
  //var newText = text.split(' ').join('');
  var temp =
      text.split('')
      .map((e) => e.codeUnitAt(0).toRadixString(2).toString());
  List<String> tripled = [];
  for (var e in temp) {
    var tempCode = '';
    for (var i = 0; i < e.length; i++) {
      tempCode += e[i] + e[i] + e[i];
    }
    while (tempCode.length < 24){
      tempCode = "000" + tempCode;
    }
    tripled.add(tempCode);
  }
  return tripled.join('');
}

decode(bits) {
  // errors: 101, 110, 011, 010, 001, 100
  var fixed = [];
  var tempFixed = [];
  for (var i = 0; i < bits.length; i += 3) {
    switch (bits.substring(i, i + 3)) {
      case '101':
      case '110':
      case '011':
      case '111':
        tempFixed.add('1');
        break;
      case '010':
      case '001':
      case '100':
      case '000':
        tempFixed.add('0');
        break;
    }
    if (tempFixed.length == 8) {
      fixed.add(tempFixed.join(''));
      tempFixed = [];
    }
  }
  return String.fromCharCodes(fixed.map((v) => int.parse(v, radix: 2)));
}

